import React, { useEffect, useState } from "react";
import { fade, makeStyles } from "@material-ui/core/styles";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import SearchBar from "./SearchBar";
import NavAllProducts from "./NavAllProducts";
import NavContent from "./NavContent";
import axios from "axios";


const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  appColor: {
    backgroundColor: "#ffcccb",
  },
  demo2: {
    backgroundColor: "#009879",
    width: "60%",
    margin: "auto",
    color: "white",
  },
  table: {
    width: "60%",
  },
  title: {
    flexGrow: 1,
    display: "none",
    [theme.breakpoints.up("sm")]: {
      display: "block",
    },
  },
  search: {
    position: "relative",
    borderRadius: theme.shape.borderRadius,
    backgroundColor: fade(theme.palette.common.white, 0.15),
    "&:hover": {
      backgroundColor: fade(theme.palette.common.white, 0.25),
    },
    marginRight: theme.spacing(2),
    marginLeft: 0,
    width: "100%",
    [theme.breakpoints.up("sm")]: {
      marginLeft: theme.spacing(3),
      width: "auto",
    },
  },
  searchIcon: {
    padding: theme.spacing(0, 2),
    height: "100%",
    position: "absolute",
    pointerEvents: "none",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  inputRoot: {
    color: "inherit",
  },
  inputInput: {
    padding: theme.spacing(1, 1, 1, 0),
    // vertical padding + font size from searchIcon
    paddingLeft: `calc(1em + ${theme.spacing(4)}px)`,
    transition: theme.transitions.create("width"),
    width: "100%",
    [theme.breakpoints.up("sm")]: {
      width: "12ch",
      "&:focus": {
        width: "20ch",
      },
    },
  },
}));

function Navbar(props) {
  const classes = useStyles();
  const [allCategories, setAllCategories] = useState([]);
  //waiting for fetching data
  const [isLoading, setLoading] = useState(true);
  //getting categories from API

  const getCategories = async (props) => {
    const res = await axios.get("https://api.predic8.de/shop/categories/");
    const categories = res.data.categories;
    //removing every element w/o name or number as name
    const filtered = await categories.filter((element) => {
      return element.hasOwnProperty("name") && isNaN(element.name);
    });
    //creating array of product urls
    const categorieUrl = filtered.map((element) => {
      return props.url + element.category_url;
    });
    //promise for every url
    const getproductsByCategorie = await categorieUrl.map((url) => {
      return axios
        .get(url)
        .then((res) => res.data)
        .catch((err) => console.log(err));
    });

    //set allCategories in Array of Objects: [...{name: "", products: []}]
    await axios
      .all(getproductsByCategorie)
      .then((res) => setAllCategories(res));
    setLoading(false);
  };

  useEffect(() => {
    getCategories();
  }, []);

  //handle Tabs
  const [tabValue, setTabValue] = useState(0);
  const handleTabs = (event, newValue) => {
    setTabValue(newValue);
  };



  return (
    <div className={classes.root}>
      <AppBar position="static" elevation={0} className={classes.appColor}>
        <Toolbar>
          <Typography className={classes.title} variant="h6" noWrap>
            Fruity
          </Typography>
          <Tabs onChange={handleTabs} value={tabValue} variant="scrollable">
            {allCategories.map((category, index) => {
              return <Tab label={category.name} key={index}></Tab>;
            })}
            <Tab label="All Products" />
          </Tabs>
          {allCategories.map((category, index) => {
            return (
              <NavContent
                key={index}
                value={tabValue}
                data={category.products}
                index={index}
              />
            );
          })}
          <NavAllProducts
            value={tabValue}
            data={allCategories}
            index={allCategories.length}
          />
          <SearchBar />
        </Toolbar>
      </AppBar>
    </div>
  );
}

export default Navbar;
